const fs = require('fs');

const FILE_EXTENSION = '.original';
const S3_STANDARD_REGION = 'us-east-1';

let Progressbar;


const S3 = module.exports = function S3(Aws, generator) {
    this.Aws = Aws;
    try {
        Progressbar = require('progress'); // eslint-disable-line
    } catch (e) {
        generator.error(`Something went wrong while running jhipster:aws:\n${e}`);
    }
};

S3.prototype.createBucket = function createBucket(params, callback) {
    const bucket = params.bucket;
    const region = this.Aws.config.region;

    const s3Params = {
        Bucket: bucket,
        CreateBucketConfiguration: { LocationConstraint: region }
    };

    if (region.toLowerCase() === S3_STANDARD_REGION) {
        s3Params.CreateBucketConfiguration = undefined;
    }

    const s3 = new this.Aws.S3({
        params: s3Params,
        signatureVersion: 'v4'
    });

    s3.headBucket((err) => {
        if (err && err.statusCode === 404) {
            s3.createBucket((err) => {
                if (err) {
                    error(err.message, callback);
                } else {
                    success(`Bucket ${bucket} created successful`, callback);
                }
            });
        } else if (err && err.statusCode === 301) {
            error(`Bucket ${bucket} is already in use`, callback);
        } else if (err) {
            error(err.message, callback);
        } else {
            success(`Bucket ${bucket} already exists`, callback);
        }
    });
};

S3.prototype.uploadWar = function uploadWar(params, callback) {
    const bucket = params.bucket;
    const buildTool = params.buildTool;
    let buildFolder;

    if (buildTool === 'gradle') {
        buildFolder = 'build/libs/';
    } else {
        buildFolder = 'target/';
    }

    findWarFilename(buildFolder, (err, warFilename) => {
        if (err) {
            error(err, callback);
        } else {
            const warKey = warFilename.slice(0, -FILE_EXTENSION.length);

            const s3 = new this.Aws.S3({
                params: {
                    Bucket: bucket,
                    Key: warKey
                },
                signatureVersion: 'v4',
                httpOptions: { timeout: 600000 }
            });

            const filePath = buildFolder + warFilename;
            const body = fs.createReadStream(filePath);

            uploadToS3(s3, body, (err, message) => {
                if (err) {
                    error(err.message, callback);
                } else {
                    callback(null, { message, warKey });
                }
            });
        }
    });
};

function findWarFilename(buildFolder, callback) {
    let warFilename = '';
    fs.readdir(buildFolder, (err, files) => {
        if (err) {
            error(err, callback);
        }
        files.filter(file => file.substr(-FILE_EXTENSION.length) === FILE_EXTENSION)
        .forEach((file) => {
            warFilename = file;
        });
        callback(null, warFilename);
    });
}

function uploadToS3(s3, body, callback) {
    let bar;

    s3.waitFor('bucketExists', (err) => {
        if (err) {
            callback(err, null);
        } else {
            s3.upload({ Body: body }).on('httpUploadProgress', (evt) => {
                if (bar === undefined && evt.total) {
                    const total = evt.total / 1000000;
                    bar = new Progressbar('uploading [:bar] :percent :etas', {
                        complete: '=',
                        incomplete: ' ',
                        width: 20,
                        total,
                        clear: true
                    });
                }

                const curr = evt.loaded / 1000000;
                bar.tick(curr - bar.curr);
            }).send((err) => {
                if (err) {
                    callback(err, null);
                } else {
                    callback(null, 'War uploaded successful');
                }
            });
        }
    });
}

function success(message, callback) {
    callback(null, { message });
}

function error(message, callback) {
    callback({ message }, null);
}

let aws;

const Rds = module.exports = function Rds(Aws) {
    aws = Aws;
};

Rds.prototype.createDatabase = function createDatabase(params, callback) {
    const dbInstanceClass = params.dbInstanceClass;
    const dbName = params.dbName;
    const dbEngine = params.dbEngine;
    const dbPassword = params.dbPassword;
    const dbUsername = params.dbUsername;

    createRdsSecurityGroup({ rdsSecurityGroupName: dbName }, (err, data) => {
        if (err) {
            callback({ message: err.message }, null);
        } else {
            const rdsSecurityGroupId = data.rdsSecurityGroupId;

            if (!rdsSecurityGroupId) {
                callback(null, { message: `Database ${dbName} already exists` });
            } else {
                authorizeSecurityGroupIngress({ rdsSecurityGroupId }, (err) => {
                    if (err) {
                        callback({ message: err.message }, null);
                    } else {
                        createDbInstance({
                            dbInstanceClass,
                            dbName,
                            dbEngine,
                            dbPassword,
                            dbUsername,
                            rdsSecurityGroupId
                        }, (err, data) => {
                            if (err) {
                                callback({ message: err.message }, null);
                            } else {
                                callback(null, { message: data.message });
                            }
                        });
                    }
                });
            }
        }
    });
};

Rds.prototype.createDatabaseUrl = function createDatabaseUrl(params, callback) {
    const rds = new aws.RDS();
    const dbName = params.dbName;
    const dbEngine = params.dbEngine;

    rds.waitFor('dBInstanceAvailable', { DBInstanceIdentifier: dbName }, (err, data) => {
        if (err) {
            callback(err, null);
        } else {
            const dbEndpoint = data.DBInstances[0].Endpoint;
            const dbUrl = `jdbc:${dbEngine}://${dbEndpoint.Address}:${dbEndpoint.Port}/${dbName}`;
            const message = `Database available at ${dbUrl}`;
            callback(null, { message, dbUrl });
        }
    });
};

function createRdsSecurityGroup(params, callback) {
    const ec2 = new aws.EC2();

    const securityGroupParams = {
        Description: 'Enable database access to Beanstalk application',
        GroupName: params.rdsSecurityGroupName
    };

    ec2.createSecurityGroup(securityGroupParams, (err, data) => {
        if (err && err.code === 'InvalidGroup.Duplicate') {
            callback(null, { message: `Security group ${params.rdsSecurityGroupName} already exists` });
        } else if (err) {
            callback(err, null);
        } else {
            callback(null, { rdsSecurityGroupId: data.GroupId });
        }
    });
}

function authorizeSecurityGroupIngress(params, callback) {
    const ec2 = new aws.EC2();

    const securityGroupParams = {
        GroupId: params.rdsSecurityGroupId,
        IpProtocol: 'tcp',
        FromPort: 0,
        ToPort: 65535,
        CidrIp: '0.0.0.0/0'
    };

    ec2.authorizeSecurityGroupIngress(securityGroupParams, (err) => {
        if (err) {
            callback(err, null);
        } else {
            callback(null, { message: 'Create security group successful' });
        }
    });
}

function createDbInstance(params, callback) {
    const rds = new aws.RDS();

    const dbInstanceParams = {
        AllocatedStorage: 5,
        DBInstanceClass: params.dbInstanceClass,
        DBInstanceIdentifier: params.dbName,
        Engine: params.dbEngine,
        MasterUserPassword: params.dbPassword,
        MasterUsername: params.dbUsername,
        DBName: params.dbName,
        VpcSecurityGroupIds: [params.rdsSecurityGroupId],
        MultiAZ: false,
        Iops: 0
    };

    rds.createDBInstance(dbInstanceParams, (err) => {
        if (err && err.code === 'DBInstanceAlreadyExists') {
            callback(null, { message: 'Database already exists' });
        } else if (err) {
            callback(err, null);
        } else {
            callback(null, { message: 'Database created successful' });
        }
    });
}

let aws;
let uuidV4;

const Eb = module.exports = function Eb(Aws, generator) {
    aws = Aws;
    try {
        uuidV4 = require('uuid/v4'); // eslint-disable-line
    } catch (e) {
        generator.error(`Something went wrong while running jhipster:aws:\n${e}`);
    }
};

Eb.prototype.createApplication = function createApplication(params, callback) {
    const applicationName = params.applicationName;
    const bucketName = params.bucketName;
    const warKey = params.warKey;
    const versionLabel = `${this.warKey}-${uuidV4()}`;
    const environmentName = params.environmentName;
    const dbUrl = params.dbUrl;
    const dbUsername = params.dbUsername;
    const dbPassword = params.dbPassword;
    const instanceType = params.instanceType;

    const applicationParams = {
        applicationName,
        versionLabel,
        bucketName,
        warKey
    };

    createApplicationVersion(applicationParams, (err) => {
        if (err) {
            callback({ message: err.message }, null);
        } else {
            const environmentParams = {
                applicationName,
                environmentName,
                versionLabel,
                dbUrl,
                dbUsername,
                dbPassword,
                instanceType
            };

            checkEnvironment(environmentParams, (err, data) => {
                if (err) {
                    callback({ message: err.message }, null);
                } else if (data.environmentExists) {
                    updateEnvironment(environmentParams, (err, data) => {
                        if (err) {
                            callback({ message: err.message }, null);
                        } else {
                            callback(null, { message: data.message });
                        }
                    });
                } else {
                    createEnvironment(environmentParams, (err, data) => {
                        if (err) {
                            callback({ message: err.message }, null);
                        } else {
                            callback(null, { message: data.message });
                        }
                    });
                }
            });
        }
    });
};

function createApplicationVersion(params, callback) {
    const applicationName = params.applicationName;
    const versionLabel = params.versionLabel;
    const bucketName = params.bucketName;
    const warKey = params.warKey;

    const elasticbeanstalk = new aws.ElasticBeanstalk();

    const applicationParams = {
        ApplicationName: applicationName,
        VersionLabel: versionLabel,
        AutoCreateApplication: true,
        SourceBundle: {
            S3Bucket: bucketName,
            S3Key: warKey
        }
    };

    elasticbeanstalk.createApplicationVersion(applicationParams, (err) => {
        if (err) {
            callback(err, null);
        } else {
            callback(null, { message: `Application version ${applicationName} created successful` });
        }
    });
}

function checkEnvironment(params, callback) {
    const applicationName = params.applicationName;
    const environmentName = params.environmentName;

    const elasticbeanstalk = new aws.ElasticBeanstalk();

    const environmentParams = {
        ApplicationName: applicationName,
        EnvironmentNames: [environmentName]
    };

    elasticbeanstalk.describeEnvironments(environmentParams, (err, data) => {
        if (data.Environments.length === 0) {
            callback(null, { message: `Environment ${environmentName} not exists`, environmentExists: false });
        } else if (err) {
            callback(err, null);
        } else {
            callback(null, { message: `Environment ${environmentName} already exists`, environmentExists: true });
        }
    });
}

function createEnvironment(params, callback) {
    const applicationName = params.applicationName;
    const environmentName = params.environmentName;
    const dbUrl = params.dbUrl;
    const dbUsername = params.dbUsername;
    const dbPassword = params.dbPassword;
    const instanceType = params.instanceType;
    const versionLabel = params.versionLabel;

    const elasticbeanstalk = new aws.ElasticBeanstalk();

    getLatestSolutionStackName((err, data) => {
        if (err) callback(err, null);

        const solutionStackName = data.solutionStackName;
        const environmentParams = {
            ApplicationName: applicationName,
            EnvironmentName: environmentName,
            OptionSettings: [
                {
                    Namespace: 'aws:elasticbeanstalk:application:environment',
                    OptionName: 'spring.profiles.active',
                    Value: 'prod'
                },
                {
                    Namespace: 'aws:elasticbeanstalk:application:environment',
                    OptionName: 'spring.datasource.url',
                    Value: dbUrl
                },
                {
                    Namespace: 'aws:elasticbeanstalk:application:environment',
                    OptionName: 'spring.datasource.username',
                    Value: dbUsername
                },
                {
                    Namespace: 'aws:elasticbeanstalk:application:environment',
                    OptionName: 'spring.datasource.password',
                    Value: dbPassword
                },
                {
                    Namespace: 'aws:autoscaling:launchconfiguration',
                    OptionName: 'InstanceType',
                    Value: instanceType
                },
                {
                    Namespace: 'aws:autoscaling:launchconfiguration',
                    OptionName: 'IamInstanceProfile',
                    Value: 'aws-elasticbeanstalk-ec2-role'
                }
            ],
            SolutionStackName: solutionStackName,
            VersionLabel: versionLabel,
            Tier: {
                Name: 'WebServer',
                Type: 'Standard'
            }
        };

        elasticbeanstalk.createEnvironment(environmentParams, (err) => {
            if (err) callback(err, null);
            else callback(null, { message: `Environment ${environmentName} created successful` });
        });
    });
}

function getLatestSolutionStackName(callback) {
    const elasticbeanstalk = new aws.ElasticBeanstalk();

    elasticbeanstalk.listAvailableSolutionStacks((err, data) => {
        if (err) callback(err, null);
        filterSolutionStackNames(data, callback);
    });

    function filterSolutionStackNames(data, callback) {
        const filteredArray = data.SolutionStacks.filter(filterCriteria);
        callback(null, { solutionStackName: filteredArray[0] });
    }

    function filterCriteria(element) {
        return element.indexOf('Tomcat 8') > -1;
    }
}

function updateEnvironment(params, callback) {
    const environmentName = params.environmentName;
    const instanceType = params.instanceType;
    const versionLabel = params.versionLabel;

    const elasticbeanstalk = new aws.ElasticBeanstalk();

    const environmentParams = {
        EnvironmentName: environmentName,
        OptionSettings: [
            {
                Namespace: 'aws:autoscaling:launchconfiguration',
                OptionName: 'InstanceType',
                Value: instanceType
            }
        ],
        VersionLabel: versionLabel
    };

    elasticbeanstalk.updateEnvironment(environmentParams, (err) => {
        if (err) {
            callback(err, null);
        } else {
            callback(null, { message: `Environment ${environmentName} updated successful` });
        }
    });
}

